/** @jsxImportSource theme-ui */

import React, { useEffect } from "react"
import ReactDOM from "react-dom"

import grapesjs from 'grapesjs';
import Basics from 'grapesjs-blocks-basic';

import BaseReactComponent from './base-react-component';
import ReactComponents from './react-components';
import MuiComponents from './mui-components';

import { ThemeProvider } from 'theme-ui'
import { funk } from '@theme-ui/presets'




const Index = () => {
  useEffect(() => {
    const Editor = grapesjs.init({
      container: '#gjs',
      height: '100%',
      storageManager: {
        id: 'gjs-',             // Prefix identifier that will be used inside storing and loading
        type: 'local',          // Type of the storage
        autosave: true,         // Store data automatically
        autoload: true,         // Autoload stored data on init
        stepsBeforeSave: 1,     // If autosave enabled, indicates how many changes are necessary before store method is triggered
        storeComponents: true,  // Enable/Disable storing of components in JSON format
        storeStyles: true,      // Enable/Disable storing of rules in JSON format
        storeHtml: true,        // Enable/Disable storing of components as HTML string
        storeCss: true,         // Enable/Disable storing of rules as CSS string
      },
      panels: {defaults: [{
        id: 'commands',
      }]},
      noticeOnUnload: false,
      plugins: [Basics, BaseReactComponent, ReactComponents, MuiComponents]
    });

    grapesjs.init({
      container  : '#gjs2',
      height: '100%',
      fromElement: true,
      storageManager: { type: 0 },
      plugins: [Basics, BaseReactComponent, ReactComponents, MuiComponents],
      blockManager: {
        appendTo: '#blocks'
      },
      styleManager: {
        appendTo: '#style-manager-container',
        sectors: [{
      name: 'General',
      open: false,
      buildProps: ['float', 'display', 'position', 'top', 'right', 'left', 'bottom']
    },{
      name: 'Dimension',
      open: false,
      buildProps: [ 'width', 'height', 'max-width', 'min-height', 'margin', 'padding']
    },{
      name: 'Typography',
      open: false,
      buildProps: ['font-family', 'font-size', 'font-weight', 'letter-spacing', 'color', 'line-height', 'text-align', 'text-shadow']
    },{
      name: 'Decorations',
      open: false,
      buildProps: ['border-radius-c', 'background-color', 'border-radius', 'border', 'box-shadow', 'background'],
    },{
      name: 'Extra',
      open: false,
      buildProps: ['opacity', 'transition', 'perspective', 'transform'],
      properties: [{
        type: 'slider',
        property: 'opacity',
        defaults: 1,
        step: 0.01,
        max: 1,
        min:0,
      }]
    }],
      },
      selectorManager: {
        appendTo: '#selectors-container',
      },
      traitManager: {
        appendTo: '#traits-container',
      },
      panels: {
        defaults: [{
          id: 'styles',
          el: '#style-manager',
          resizable: {
            tc: 0,
            cr: 0,
            cl: 1,
            bc: 0,
            keyWidth: 'flex-basis',
          },
        }]
      }
    });

const bm = editor.BlockManager;
editor.on('load', () => {
  editor.BlockManager.render([
    bm.get('column1').set('category', ''),
    bm.get('column2').set('category', ''),
    bm.get('column3').set('category', ''),
    bm.get('text').set('category', ''),
    bm.get('image').set('category', '')
  ])
});

editor.runCommand('sw-visibility');

    editor.setComponents(`
    <div>
      <span>
        Foo
      </span>
      <Listing>
        <div>
          Bar
        </div>
        <MuiButton variant='contained' color='primary'>
          Click Me
        </MuiButton>
        <Slider />
      </Listing>
      <Snackbar>
        <MuiButton variant='contained' color='secondary'>
          Click Me
        </MuiButton>
      </Snackbar>
      <Slider />
    </div>
    `);
  });
  return (
    <>
      <header>
        test
      </header>
      <div id='gjs' />
      <footer>
        Footer test
      </footer>
    </>
  )
} 

ReactDOM.render(
  <React.StrictMode>
    <ThemeProvider theme={funk}>
      <Index />
    </ThemeProvider>
  </React.StrictMode>,
  document.getElementById("root")
)
